mapper.select:
select
(SELECT COUNT(1) FROM t_group_complaint_record w WHERE t_group_complaint_record.group_id = w.group_id) AS complaintTotal,

from t_group_complaint_record


    owner_account = #{ownerAccount}


    AND status = #{status}


    AND DATE_FORMAT(crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(crt_time,'%Y-%m-%d') <= #{endTime}


ORDER BY crt_time DESC

  LIMIT #{start}, #{limit}

select
    COUNT(1)
from t_group_complaint_record


    owner_account = #{ownerAccount}


    AND status = #{status}


    AND DATE_FORMAT(crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(crt_time,'%Y-%m-%d') <= #{endTime}

select

from t_group_complaint_record


    id = #{id}


    AND owner_account = #{ownerAccount}
