mapper.select:
SELECT a.id AS id, a.member_account AS memberAccount, a.member_nickname AS memberNickname, a.complainant AS complainant, a.complaint_time AS complaintTime, a.complaint_remark AS complaintRemark, a.chat_record AS chatRecord, a.`status` AS `status`, a.crt_time AS crtTime, a.audit_opinion AS auditOpinion, IFNULL( b.wallet_balance, '0' ) AS walletBalance, IFNULL( b.advertising_fee, '0' ) AS advertisingFee, IFNULL( d.team_user_name, '-' ) AS recomNickname,
FROM t_member_complaint_record AS a LEFT JOIN t_member b ON a.member_account = b.member_account LEFT JOIN zx_app_my_team d ON a.member_account = d.team_recommended_user_id
a.member_account = #{memberAccount}
AND a.status = #{status}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT
    COUNT(1)
FROM
t_member_complaint_record AS a


    a.member_account = #{memberAccount}


    AND a.status = #{status}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
