mapper.select:
SELECT a.id AS id, IFNULL( a.member_account, '' ) AS memberAccount, IFNULL( a.nickname, '' ) AS nickname, IFNULL( a.recom_account, '' ) AS recomAccount, IFNULL( a.recom_nick_name, '-' ) AS recomNickname, IFNULL( b.real_name, '' ) AS realName, IFNULL( a.wallet_balance, '0' ) AS walletBalance, IFNULL( a.advertising_fee, '0' ) AS advertisingFee, IFNULL( a.STATUS, '' ) AS status, a.crt_time AS crtTime,
FROM t_member a LEFT JOIN t_real_name_authentic b ON a.member_account = b.member_account
a.member_account = #{memberAccount}
AND a.nickname = #{nickname}
AND a.status = #{status}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT
  COUNT(1)
FROM
  t_member a


    a.member_account = #{memberAccount}


    AND a.nickname = #{nickname}


    AND a.status = #{status}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}


    AND a.recom_account = #{recomAccount}

SELECT a.member_account AS memberAccount, a.member_nickname AS memberNickname, a.complainant AS complainant, a.complaint_time AS complaintTime, a.complaint_remark AS complaintRemark, #a.chat_record AS chatRecord, a.`status` AS `status` FROM t_member_complaint_record AS a
a.status = #{status}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT a.id AS id, IFNULL( a.member_account, '' ) AS memberAccount, IFNULL( a.nickname, '' ) AS nickname, IFNULL( a.member_pwd, '' ) AS memberPwd, IFNULL( a.head_img_url, '' ) AS headImgUrl, IFNULL( b.real_name, '') AS realName, IFNULL( a.member_payment_pwd, '' ) AS memberPaymentPwd, IFNULL( a.recom_nick_name, '-' ) AS recomNickname, IFNULL( a.wallet_balance, '0' ) AS walletBalance, IFNULL( a.red_enve_balance, '0' ) AS redEnveBalance, IFNULL( a.advertising_fee, '0' ) AS advertisingFee, IFNULL( a.STATUS, '' ) AS status, IFNULL( a.bind_phone, '' ) AS bindPhone, a.crt_time AS crtTime, a.zx_my_team_id as zxMyTeamId FROM t_member a LEFT JOIN t_real_name_authentic b ON a.member_account = b.member_account
a.id = #{id, jdbcType=BIGINT}
AND a.member_account = #{memberAccount}
