mapper.select:
SELECT a.zx_message_id AS zxMessageId, a.member_account AS memberAccount, a.zx_message_link_url AS zxMessageLinkUrl, a.zx_message_video_url AS zxMessageVideoUrl, a.zx_message_date AS zxMessageDate, a.zx_message_text AS zxMessageText, ( SELECT COUNT( 1 ) FROM zx_app_message_view_count v WHERE v.zx_message_id = a.zx_message_id ) AS zxMessageViewCount, b.head_img_url AS headImgUrl, b.nickname AS nickname FROM zx_app_message AS a LEFT JOIN t_member b ON a.member_account = b.member_account
a.member_account = #{memberAccount,jdbcType=VARCHAR}
ORDER BY zx_message_date DESC
LIMIT ${start},${limit}

SELECT
  COUNT(1) AS praiseTotal
FROM
  `zx_app_message_praise_count`


    zx_message_id = #{zxMessageId}


    AND member_account = #{memberAccount}

SELECT a.zx_message_id AS zxMessageId, a.member_account AS zxMemberAccount, a.crt_time AS crtTime, b.head_img_url AS headImgUrl, b.nickname AS nickname, c.zx_message_text AS zxMessageText FROM zx_app_message_collection AS a LEFT JOIN zx_app_message AS c ON a.zx_message_id = c.zx_message_id LEFT JOIN t_member AS b ON c.member_account = b.member_account
a.member_account = #{memberAccount}
LIMIT ${start},${limit}
