mapper.select:
SELECT rn.id AS id, rn.code AS code, rn.name AS name, rnt.id AS noticeTypeId, rnt.name AS noticeTypeName, rn.status AS statusId, rccv.cmn_cd_val_nm AS status, rn.instancy_level_id AS instancyLevelId, rn.instancy_level_name AS instancyLevelName, rn.publish_id AS publishId, rn.publish_name AS publishName, rn.publish_time AS publishTime, (CASE rn.send_total when -9999 then (select count(1) from t_u_admin where valid_sts_cd=1) ELSE rn.send_total END ) AS sendTotal, rn.publish_department AS publishDepartmentId, ro.organiza_name AS publishDepartment, IFNULL((select count(id) from r_notice_read where rn.id = notice_id GROUP BY notice_id),0) as readTotal FROM r_notice rn LEFT JOIN r_notice_type rnt ON rn.notice_type_id=rnt.id LEFT JOIN r_organiza ro ON ro.organiza_id = rn.publish_department LEFT JOIN r_cmn_cd_val rccv ON rccv.cmn_cd_val = rn.status AND rccv.cmn_cd='1002'
rn.publish_id = #{userId}
AND rn.code LIKE concat('%',#{code},'%')
AND rn.crt_time>=#{beginTime}
AND rn.crt_time<=#{endTime}
AND rn.name like concat('%',#{name},'%')
AND rn.status=#{status}
ORDER BY rn.publish_time DESC
LIMIT ${start},${limit}

SELECT
  COUNT(1)
FROM
  r_notice


        AND code LIKE  concat('%',#{code},'%')


        AND crt_time>=#{beginTime}


        AND crt_time<=#{endTime}


        AND name like concat('%',#{name},'%')


        AND status=#{status}

SELECT rn.id AS id, rn.code AS code, rn.name AS name, rnt.id AS noticeTypeId, rnt.name AS noticeTypeName, rn.status AS statusId, rccv.cmn_cd_val_nm AS status, rn.instancy_level_id AS instancyLevelId, rn.instancy_level_name AS instancyLevelName, rn.publish_id AS publishId, rn.publish_name AS publishName, (CASE rn.send_total when -9999 then (select count(1) from t_u_admin where valid_sts_cd=1) ELSE rn.send_total END ) AS sendTotal, rn.publish_department AS publishDepartmentId, ro.organiza_name AS publishDepartment, IFNULL((select count(id) from r_notice_read where rn.id = notice_id GROUP BY notice_id),0) as readTotal FROM r_notice rn LEFT JOIN r_notice_type rnt ON rn.notice_type_id=rnt.id LEFT JOIN r_organiza ro ON ro.organiza_id = rn.publish_department LEFT JOIN r_cmn_cd_val rccv ON rccv.cmn_cd_val = rn.status AND rccv.cmn_cd='1002'
AND rn.code LIKE concat('%',#{code},'%')
AND rn.crt_time>=#{beginTime}
AND rn.crt_time<=#{endTime}
AND rn.name like concat('%',#{name},'%')
AND rn.status=#{status}
ORDER BY rn.publish_time desc
LIMIT ${start},${limit}

SELECT
  COUNT(1)
FROM
  r_notice

    publish_id = #{userId}

        AND code LIKE  concat('%',#{code},'%')


        AND crt_time>=#{beginTime}


        AND crt_time<=#{endTime}


        AND name like concat('%',#{name},'%')


        AND status=#{status}

SELECT count(1) FROM r_notice rn
rn.status = '1003'
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}

SELECT count(1) FROM r_notice_permission rnp LEFT JOIN r_notice rn ON rnp.notice_id = rn.id
rnp.crt_id in(#{userId},-9999) AND rnp.status = 1
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}

SELECT rn.id as id, rn.code AS code, rn.name AS name, rn.notice_type_name as noticeTypeName, rn.instancy_level_name as instancyLevelName, rn.publish_name as publishName, rn.publish_time as publishTime, rn.publish_department as publishDepartmentId, ro.organiza_name as publishDepartment, '' as readTime, 0 as isRead FROM r_notice rn LEFT JOIN r_organiza ro ON ro.organiza_id = rn.publish_department
rn.status = '1003' AND rn.id NOT IN(select notice_id from r_notice_read where crt_id = #{userId})
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}
UNION SELECT rn.id as id, rn.code AS code, rn.name AS name, rn.notice_type_name as noticeTypeName, rn.instancy_level_name as instancyLevelName, rn.publish_name as publishName, rn.publish_time as publishTime, rn.publish_department as publishDepartmentId, ro.organiza_name as publishDepartment, rnr.read_time as readTime, rnr.status as isRead FROM r_notice rn LEFT JOIN r_notice_read rnr ON rnr.notice_id = rn.id LEFT JOIN r_organiza ro ON ro.organiza_id = rn.publish_department
rn.status = '1003' and rnr.crt_id =#{userId}
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}
ORDER BY publishTime DESC
LIMIT ${start},${limit}

SELECT rn.id as id, rn.code AS code, rn.name AS name, rn.publish_time AS publishTime, rn.notice_type_name as noticeTypeName, rn.instancy_level_name as instancyLevelName, rn.publish_name as publishName, rn.publish_department as publishDepartmentId, ro.organiza_name as publishDepartment, '' as readTime, 0 as isRead FROM r_notice_permission rnp LEFT JOIN r_notice rn ON rnp.notice_id = rn.id LEFT JOIN r_notice_read rnr ON rn.id = rnr.notice_id LEFT JOIN r_organiza ro ON ro.organiza_id = rn.publish_department
rnp.crt_id in(#{userId},-9999) AND rnp.status = 1 AND rnp.notice_id NOT IN (select notice_id from r_notice_read where crt_id=#{userId})
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}
UNION SELECT rn.id as id, rn.code AS code, rn.name AS name, rn.publish_time AS publishTime, rn.notice_type_name as noticeTypeName, rn.instancy_level_name as instancyLevelName, rn.publish_name as publishName, rn.publish_department as publishDepartmentId, ro.organiza_name as publishDepartment, rnr.read_time as readTime, rnr.status as isRead FROM r_notice rn LEFT JOIN r_notice_read rnr ON rnr.notice_id = rn.id LEFT JOIN r_organiza ro ON ro.organiza_id = rn.publish_department
rnr.crt_id = #{userId} and rn.status = '1003'
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}
ORDER BY publishTime DESC
LIMIT ${start},${limit}

SELECT count(1) FROM r_notice rn LEFT JOIN r_notice_read rnr ON rnr.notice_id = rn.id
rn.status = '1003' and rnr.crt_id = #{userId}
and rn.code like concat('%',#{code},'%')
and rn.name like concat('%',#{name},'%')
and rn.publish_time>=#{beginTime}
and rn.publish_time <=#{endTime}

