mapper.select:
SELECT a.id AS id, a.member_account AS memberAccount, a.recharge_amount AS rechargeAmount, a.recharge_time, a.`status` AS `status`, a.crt_time AS crtTime, b.nickname AS nickname, IFNULL( d.team_user_name, '-' ) AS recomNickname, b.advertising_fee AS advertisingFee, b.wallet_balance AS walletBalance, b.`status` AS memberStatus, c.real_name AS realName,
FROM t_recharge_record AS a LEFT JOIN t_member b ON a.member_account = b.member_account LEFT JOIN t_real_name_authentic c ON a.member_account = c.member_account LEFT JOIN zx_app_my_team d ON a.member_account = d.team_recommended_user_id
a.member_account = #{memberAccount}
AND b.nickname = #{nickname}
AND a.status = #{status}
AND b.status = #{memberStatus}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
AND a.valid_sts_cd = '1'
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT
   COUNT(1)
FROM
    t_recharge_record AS a
LEFT JOIN t_member b ON a.member_account = b.member_account
LEFT JOIN t_real_name_authentic c ON a.member_account = c.member_account


    a.member_account = #{memberAccount}


    AND b.nickname = #{nickname}


    AND a.status = #{status}


    AND b.status = #{memberStatus}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}

  AND a.valid_sts_cd = '1'
