mapper.select:
SELECT a.red_packet_img_id AS redPacketId, a.member_account AS redPacketUserId, a.red_packet_img_money_count AS redPacketMoneyCount, a.red_packet_img_count AS redPacketCount, a.red_packet_img_stock AS redPacketStock, a.red_packet_img_url AS redPacketUrl, a.red_packet_img_info AS redPacketInfo, a.red_packet_img_luck_star AS redPacketLuckStar, a.red_packet_img_start_time AS redPacketStartTime, a.red_packet_img_end_time AS redPacketEndTime, a.red_packet_img_question AS redPacketQuestion, a.red_packet_img_options AS redPacketOptions, a.red_packet_img_answer AS redPacketAnswer, a.red_packet_img_sex AS redPacketSex, a.red_packet_img_age_start AS redPacketAgeStart, a.red_packet_img_age_end AS redPacketAgeEnd, a.red_packet_img_date AS redPacketDate, a.red_packet_img_type AS redPacketType, b.head_img_url AS headImgUrl, #b.nickname AS nickname, IFNULL(b.nickname,b.member_account) AS nickname, -- 发布红包用户昵称 b.member_account AS memberAccount, a.province_name AS provinceName, a.city_name AS cityName, a.county_name AS countyName FROM zx_app_img_red_packet a LEFT JOIN t_member b ON a.member_account = b.member_account
a.red_packet_img_id = #{redPacketImgId}
ORDER BY a.red_packet_img_date ASC
