mapper.select:
SELECT a.red_packet_id AS redPacketId, a.member_account AS redPacketUserId, a.red_packet_money_count AS redPacketMoneyCount, a.red_packet_count AS redPacketCount, a.red_packet_stock AS redPacketStock, a.red_packet_info AS redPacketInfo, a.red_packet_luck_star AS redPacketLuckStar, a.red_packet_start_time AS redPacketStartTime, a.red_packet_end_time AS redPacketEndTime, a.red_packet_question AS redPacketQuestion, a.red_packet_options AS redPacketOptions, a.red_packet_answer AS redPacketAnswer, a.red_packet_sex AS redPacketSex, a.red_packet_age_start AS redPacketAgeStart, a.red_packet_age_end AS redPacketAgeEnd, a.red_packet_date AS redPacketDate, b.head_img_url AS headImgUrl, b.nickname AS nickname, b.member_account AS memberAccount, a.province_name AS provinceName, a.city_name AS cityName, a.county_name AS countyName FROM zx_app_red_packet a LEFT JOIN t_member b ON a.member_account = b.member_account
a.red_packet_id = #{redPacketId}
ORDER BY a.red_packet_date ASC
