mapper.select:
SELECT a.zx_friend_grouping_id AS zxFriendGroupingId, a.zx_friend_id AS zxFriendId, a.zx_friend_grouping_name AS zxFriendGroupingName, a.zx_friend_grouping_type AS zxFriendGroupingType, a.zx_friend_crt_time AS zxFriendGroupingCrtTime FROM zx_app_friend_grouping AS a
a.zx_friend_id = #{zxFriendUserId}
AND zx_friend_grouping_type = #{zxFriendGroupingType}

SELECT a.zx_friend_grouping_id AS zxFriendGroupingId, a.zx_friend_id AS zxFriendId, a.zx_friend_grouping_name AS zxFriendGroupingName, a.zx_friend_grouping_type AS zxFriendGroupingType, a.zx_friend_crt_time AS zxFriendGroupingCrtTime FROM zx_app_friend_grouping AS a
a.zx_friend_id = #{zxFriendUserId}
ORDER BY a.zx_friend_crt_time DESC
