mapper.select:
select 

from zx_app_friend


    zx_friend_id = #{zxFriendId,jdbcType=BIGINT}


    AND zx_friend_user_id = #{zxFriendUserId}


    AND zx_friend_friend_id = #{zxFriendFriendId}


    AND zx_friend_grouping_id = #{zxFriendGroupingId}

SELECT a.zx_friend_id AS zxFriendId, a.zx_friend_user_id AS zxFriendUserId, a.zx_friend_remark AS zxFriendRemark, a.zx_friend_friend_id AS zxFriendFriendId, a.zx_friend_notes_type AS zxFriendNotesType, a.zx_friend_friend_type AS zxFriendFriendType, a.zx_friend_notes_date AS zxFriendNotesDate, a.zx_friend_add_date AS zxFriendAddDate, a.zx_friend_off_notes_date AS zxFriendOffNotesDate, a.zx_friend_del_date AS zxFriendDelDate, a.zx_friend_grouping_id AS zxFriendGroupingId FROM zx_app_friend AS a
a.zx_friend_id = #{zxFriendId,jdbcType=BIGINT}
AND a.zx_friend_user_id = #{zxFriendUserId}
AND a.zx_friend_friend_id = #{zxFriendFriendId}

SELECT a.zx_friend_id AS zxFriendId, a.zx_friend_user_id AS zxFriendUserId, a.zx_friend_friend_id AS zxFriendFriendId, a.zx_friend_grouping_id AS zxFriendGroupingId, a.zx_friend_friend_type AS zxFriendFriendType, IFNULL( a.zx_friend_remark, '') AS zxFriendRemark, IFNULL( b.head_img_url, '') AS headImgUrl, IFNULL( b.nickname, b.member_account ) AS nickName FROM zx_app_friend AS a LEFT JOIN t_member AS b ON b.member_account = a.zx_friend_friend_id
zx_friend_id = #{zxFriendId,jdbcType=BIGINT}
AND zx_friend_user_id = #{zxFriendUserId}
AND zx_friend_friend_id = #{zxFriendFriendId}
AND zx_friend_grouping_id = #{zxFriendGroupingId}
AND zx_friend_friend_type = 'Y'

mapper.update:
update zx_app_friend


    zx_friend_remark = #{zxFriendRemark,jdbcType=VARCHAR},


where zx_friend_user_id = #{zxFriendUserId} AND zx_friend_friend_id = #{zxFriendFriendId}
