mapper.select:
select 

from zx_app_friend


    zx_friend_id = #{zxFriendId,jdbcType=BIGINT}


    zx_friend_user_id = #{zxFriendUserId}


    zx_friend_friend_id = #{zxFriendUserId}

SELECT a.zx_friend_id AS zxFriendId, a.zx_friend_user_id AS zxFriendUserId, a.zx_friend_remark AS zxFriendRemark, a.zx_friend_friend_id AS zxFriendFriendId, a.zx_friend_notes_type AS zxFriendNotesType, a.zx_friend_friend_type AS zxFriendFriendType, a.zx_friend_notes_date AS zxFriendNotesDate, a.zx_friend_add_date AS zxFriendAddDate, a.zx_friend_off_notes_date AS zxFriendOffNotesDate, a.zx_friend_del_date AS zxFriendDelDate, a.zx_friend_grouping_id FROM zx_app_friend AS a
a.zx_friend_id = #{zxFriendId,jdbcType=BIGINT}
a.zx_friend_user_id = #{zxFriendUserId}
a.zx_friend_friend_id = #{zxFriendUserId}
