mapper.select:
SELECT , b.wallet_balance AS walletBalance, b.advertising_fee AS advertisingFee, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname, b.member_account) AS nickname, c.member_account AS shareMemberAccount, d.business_type AS businessType, (SELECT COUNT(1) FROM zx_app_goods_view_count s WHERE s.zx_goods_id = a.id) AS followNum FROM zx_app_lucky_goods a LEFT JOIN t_member b ON a.member_account = b.member_account LEFT JOIN zx_app_my_share_extend c ON a.member_account = c.cover_member_account LEFT JOIN zx_app_business_auth d ON a.member_account = d.member_account
a.member_account = #{memberAccount}
AND a.goods_status = #{goodsStatus}
AND a.goods_type_id = #{goodsTypeId}
AND a.goods_desc LIKE CONCAT('%',#{goodsDesc},'%')
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT
    COUNT(1)
FROM zx_app_lucky_goods a
LEFT JOIN t_member b ON a.member_account = b.member_account
LEFT JOIN zx_app_my_share_extend c ON a.member_account = c.cover_member_account
LEFT JOIN zx_app_business_auth d ON a.member_account = d.member_account


    a.member_account = #{memberAccount}


    AND a.goods_status = #{goodsStatus}


    AND a.goods_desc LIKE CONCAT('%',#{goodsDesc},'%')


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}

select 

from zx_app_lucky_goods a
where a.id = #{id,jdbcType=BIGINT}

SELECT a.id AS id, a.member_account AS memberAccount, a.product_cover_url AS productCoverUrl, a.goods_desc AS goodsDesc, a.is_have_shop AS isHaveShop, a.goods_url AS goodsUrl, a.goods_explain_img AS goodsExplainImg, a.goods_parcel_price AS goodsParcelPrice, a.goods_market_price AS goodsMarketPrice, a.goods_release_num AS goodsReleaseNum, a.goods_surplus_num AS goodsSurplusNum, a.goods_type_id AS goodsTypeId, a.goods_status AS goodsStatus, a.audit_opinion AS auditOpinion, DATE_FORMAT(a.crt_time, '%Y年%m月%d日') AS crtTime, a.crt_time AS releaseTime, '1003' AS goodsType, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname, b.member_account) AS nickname, (SELECT COUNT(1) FROM zx_app_goods_view_count s WHERE s.zx_goods_id = a.id) AS followNum, (SELECT COUNT(1) FROM zx_app_order_info b WHERE b.goods_id = a.id AND b.order_status = '1001') AS deliverGoods, (SELECT COUNT(1) FROM zx_app_order_info b WHERE b.goods_id = a.id AND b.order_status = '1002') AS shippedStatus, (SELECT COUNT(1) FROM zx_app_order_info b WHERE b.goods_id = a.id AND b.order_status = '1003') AS receivedGoods FROM zx_app_lucky_goods a LEFT JOIN t_member b ON a.member_account = b.member_account
a.member_account = #{memberAccount}
AND a.goods_status IN (${goodsStatus})
AND a.goods_type_id = #{goodsTypeId}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
AND goods_status != '1003'
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

mapper.update:
update zx_app_lucky_goods


    goods_status = #{goodsStatus,jdbcType=VARCHAR},


    audit_opinion = #{auditOpinion,jdbcType=VARCHAR}


where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT seat_num as seatNum, buy_member_account as memberAccount, lucky_number as luckyNumber, lucky_order_number as luckyOrderNumber, goods_luck_star as goodsLuckStar, shopping_amount as shoppingAmount, activity_type as activityType, crt_time as crtTime FROM zx_app_free_sheet_info
seat_num = #{seatNum}
ORDER BY crt_time DESC LIMIT 1

SELECT a.id as id, a.seat_num as seatNum, a.buy_member_account as memberAccount, a.lucky_number as luckyNumber, a.lucky_order_number as luckyOrderNumber, a.goods_luck_star as goodsLuckStar, a.shopping_amount as shoppingAmount, a.activity_type as activityType, a.crt_time as crtTime, b.head_img_url AS headImgUrl, IFNULL(b.nickname,b.member_account) AS nickname FROM zx_app_free_sheet_info a LEFT JOIN t_member b ON a.buy_member_account = b.member_account
a.seat_num = #{seatNum}
AND a.lucky_order_number = #{luckyMan}

mapper.update:
update zx_app_free_sheet_info


    goods_luck_star = #{goodsLuckStar,jdbcType=INTEGER},


    activity_type = #{activityType,jdbcType=VARCHAR},




    id = #{id}


    AND seat_num = #{seatNum}

mapper.select:
SELECT a.id as id, a.seat_num as seatNum, a.buy_member_account as memberAccount, a.lucky_number as luckyNumber, a.lucky_order_number as luckyOrderNumber, a.goods_luck_star as goodsLuckStar, a.shopping_amount as shoppingAmount, a.activity_type as activityType, a.crt_time as crtTime, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname,b.member_account) AS nickname FROM zx_app_free_sheet_info a LEFT JOIN t_member b ON a.buy_member_account = b.member_account
a.seat_num = #{seatNum}
AND a.shopping_amount = #{shoppingAmount}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT COUNT(1) FROM zx_app_free_sheet_info a LEFT JOIN t_member b ON a.buy_member_account = b.member_account
a.seat_num = #{seatNum}
AND a.shopping_amount = #{shoppingAmount}

SELECT a.id as id, a.seat_num as seatNum, a.buy_member_account as memberAccount, a.lucky_number as luckyNumber, a.lucky_order_number as luckyOrderNumber, a.goods_luck_star as goodsLuckStar, a.shopping_amount as shoppingAmount, a.activity_type as activityType, DATE_FORMAT(a.crt_time, '%m月%d日 %H:%i') as crtTime, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname,b.member_account) AS nickname FROM zx_app_free_sheet_info a LEFT JOIN t_member b ON a.buy_member_account = b.member_account
a.seat_num = #{seatNum}
AND a.shopping_amount = #{shoppingAmount}
ORDER BY a.lucky_order_number ASC
LIMIT #{start}, #{limit}

SELECT
  COUNT(1)
FROM
zx_app_free_sheet_info a


    a.seat_num = #{seatNum}


    AND a.buy_member_account = #{memberAccount}
