mapper.select:
SELECT a.id AS id, a.send_goods_account AS sendGoodsAccount, a.consig_goods_account AS consigGoodsAccount, a.consig_name AS consigName, a.consig_name_phone AS consigNamePhone, a.consig_address AS consigAddress, a.express_name AS expressName, a.express_number AS expressNumber, a.order_status AS orderStatus, a.order_type AS orderType, a.goods_id AS goodsId, a.crt_time AS crtTime, IFNULL( b.nickname, b.member_account ) AS nickname, b.member_account AS memberAccount, IFNULL( b.head_img_url, '' ) AS headImgUrl FROM zx_app_order_info a LEFT JOIN t_member b ON a.consig_goods_account = b.member_account
a.id = #{id}
AND a.goods_id = #{goodsId}
AND a.send_goods_account = #{sendGoodsAccount}
AND a.consig_goods_account = #{consigGoodsAccount}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT
    COUNT(1)
FROM zx_app_order_info


    id = #{id}


    AND goods_id = #{goodsId}


    send_goods_account = #{sendGoodsAccount}


    AND consig_goods_account = #{consigGoodsAccount}

select 

from zx_app_order_info


    id = #{id,jdbcType=BIGINT}


    AND send_goods_account = #{sendGoodsAccount}


    AND order_status != #{orderStatus}


    AND order_type = #{orderType}

SELECT a.id AS id, a.order_status AS orderStatus, a.order_type AS orderType, b.product_cover_url AS productCoverUrlMS, b.needed_ad_fee AS neededAdFeeMs, b.goods_url AS goodsUrl, c.product_cover_url AS productCoverUrlFree, c.needed_ad_fee AS neededAdFeeFree, d.product_cover_url AS productCoverUrlLucky, d.goods_parcel_price AS goodsParcelPrice, DATE_FORMAT(a.crt_time,'%Y年%m月%d日') AS crtTime FROM zx_app_order_info a LEFT JOIN zx_app_zero_seckill b ON a.goods_id = b.id LEFT JOIN zx_app_free_goods c ON a.goods_id = c.id LEFT JOIN zx_app_lucky_goods d ON a.goods_id = d.id
a.consig_goods_account = #{memberAccount}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}
