SELECT a.id AS id, a.goods_id AS goodsId, a.buy_member_account AS buyMemberAccount, a.lucky_number AS luckyNumber, a.lucky_order_number AS luckyOrderNumber, a.goods_luck_star AS goodsLuckStar, DATE_FORMAT(a.crt_time,'%m月%d日 %H:%i') AS crtTime, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname, b.member_account) AS nickname FROM zx_app_zero_seckill_info AS a LEFT JOIN t_member AS b ON a.buy_member_account = b.member_account
a.goods_id = #{goodsId}
AND a.buy_member_account = #{buyMemberAccount}
ORDER BY a.lucky_order_number ASC

