mapper.select:
SELECT , a.crt_time AS releaseTime, b.wallet_balance AS walletBalance, b.advertising_fee AS advertisingFee, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname, b.member_account) AS nickname, c.member_account AS shareMemberAccount, d.business_type AS businessType, '1001' AS goodsType, (SELECT COUNT(1) FROM zx_app_zero_seckill_info s WHERE s.goods_id = a.id AND a.rotation_num = s.rotation_num) AS joinNum, (SELECT COUNT(1) FROM zx_app_goods_view_count s WHERE s.zx_goods_id = a.id) AS followNum FROM zx_app_zero_seckill a LEFT JOIN t_member b ON a.member_account = b.member_account LEFT JOIN zx_app_my_share_extend c ON a.member_account = c.cover_member_account LEFT JOIN zx_app_business_auth d ON a.member_account = d.member_account AND d.auth_status != '1003'
a.member_account = #{memberAccount}
AND a.goods_type_id = #{goodsTypeId}
AND a.goods_status = #{goodsStatus} OR (a.goods_status = '1005' AND a.goods_surplus_num > 0 AND a.goods_type_id = #{goodsTypeId})
AND a.goods_status = #{goodsStatus} OR (a.goods_status = '1005' AND a.goods_surplus_num > 0)
AND a.goods_status = #{goodsStatusHT}
AND a.goods_desc LIKE CONCAT('%',#{goodsDesc},'%')
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

select
    COUNT(1)
from zx_app_zero_seckill


    member_account = #{memberAccount}


    AND goods_type_id = #{goodsTypeId}


    AND goods_status = #{goodsStatus} OR (goods_status = '1005' AND goods_surplus_num > 0 AND goods_type_id = #{goodsTypeId})


    AND goods_status = #{goodsStatus} OR (goods_status = '1005' AND goods_surplus_num > 0)


    AND goods_status = #{goodsStatusHT}


    AND goods_desc LIKE CONCAT('%',#{goodsDesc},'%')


    AND DATE_FORMAT(crt_time,'%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(crt_time,'%Y-%m-%d') <= #{endTime}

mapper.update:
update zx_app_zero_seckill


    goods_status = #{goodsStatus,jdbcType=VARCHAR},


    audit_opinion = #{auditOpinion,jdbcType=VARCHAR}


where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT a.id AS id, a.member_account AS memberAccount, a.product_cover_url AS productCoverUrl, a.goods_desc AS goodsDesc, a.is_have_shop AS isHaveShop, a.goods_url AS goodsUrl, a.goods_explain_img AS goodsExplainImg, a.goods_parcel_price AS goodsParcelPrice, a.partake_number AS partakeNumber, a.needed_ad_fee AS neededAdFee, a.needed_ad_fee_total AS neededAdFeeTotal, a.goods_release_num AS goodsReleaseNum, a.goods_surplus_num AS goodsSurplusNum, a.goods_type_id AS goodsTypeId, a.goods_status AS goodsStatus, a.audit_opinion AS auditOpinion, DATE_FORMAT(a.crt_time, '%Y年%m月%d日') AS crtTime, a.crt_time AS releaseTime, '1001' AS goodsType, IFNULL(b.head_img_url, '') AS headImgUrl, IFNULL(b.nickname, b.member_account) AS nickname, (SELECT COUNT(1) FROM zx_app_goods_view_count s WHERE s.zx_goods_id = a.id) AS followNum, (SELECT COUNT(1) FROM zx_app_order_info b WHERE b.goods_id = a.id AND b.order_status = '1001') AS deliverGoods, (SELECT COUNT(1) FROM zx_app_order_info b WHERE b.goods_id = a.id AND b.order_status = '1002') AS shippedStatus, (SELECT COUNT(1) FROM zx_app_order_info b WHERE b.goods_id = a.id AND b.order_status = '1003') AS receivedGoods FROM zx_app_zero_seckill a LEFT JOIN t_member b ON a.member_account = b.member_account
a.member_account = #{memberAccount}
AND a.goods_status IN (${goodsStatus})
AND a.goods_type_id = #{goodsTypeId}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.crt_time,'%Y-%m-%d') <= #{endTime}
AND goods_status != '1003'
ORDER BY a.crt_time DESC
LIMIT #{start}, #{limit}

SELECT
COUNT(1)
FROM zx_app_goods_view_count a


    a.member_account = #{memberAccount}


    AND a.zx_goods_id = #{goodsId}
